import { IsNumber, IsOptional, IsString } from "class-validator";

// Define a DTO class for the query parameters
class OrderItemDto {
    @IsOptional()
    @IsString()
    name: string; // A custom parameter for the name
  
    @IsOptional()
    @IsNumber()
    age: number; // A custom parameter for the age
  
    @IsOptional()
    @IsString()
    sort: string; // A custom parameter for the sort order
  }
